INSERT INTO `wp_a55jrcpgkb_posts` VALUES("158", "1", "2017-01-16 19:25:24", "2017-01-17 02:25:24", "\n		<h3>Product / Service #1</h3>		\n			Whatever your company is most known for should go right here, whether that\'s bratwurst or baseball caps or vampire bat removal.		 \n		<h3>Product / Service #2</h3>		\n			What\'s another popular item you have for sale or trade? Talk about it here in glowing, memorable terms so site visitors have to have it.		 \n		<h3>Product / Service #3</h3>		\n			Don\'t think of this product or service as your third favorite, think of it as the bronze medalist in an Olympic medals sweep of great products/services.		 \n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/services-1-1-1.jpg\" alt=\"services-1\" itemprop=\"image\"  />\n		<h3>Talk more about your products here.</h3>		\n			<p>Tell prospective customers more about your company and the services you offer here. Replace this image with one more fitting to your business.</p>		 \n		<h3>Talk more about your products here.</h3>		\n			<p>Tell prospective customers more about your company and the services you offer here. Replace this image with one more fitting to your business.</p>		 \n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/services-2-1-1.jpg\" alt=\"services-2\" itemprop=\"image\"  />\n		<h3>Next Steps...</h3>\n		<p>This is should be a prospective customer\'s number one call to action, e.g., requesting a quote or perusing your product catalog.</p>\n	<a href=\"\" target=\"_self\" role=\"button\">\n						Call to Action\n					</a>\n", "Services", "", "publish", "closed", "closed", "", "services", "", "", "2017-01-16 19:25:24", "2017-01-17 02:25:24", "", "0", "http://hopkinsmedreview.org/?page_id=158", "0", "page", "", "0");
INSERT INTO `wp_a55jrcpgkb_posts` VALUES("156", "1", "2017-01-16 19:25:24", "2017-01-17 02:25:24", "				<a href=\"#\" target=\"_self\" itemprop=\"url\">\r\n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/home-1-1-1.jpg\" alt=\"home-1\" itemprop=\"image\"  />\r\n				</a>\r\n	<h3><a href=\"#\" target=\"_self\">Product / Service #1</a></h3>		\r\n			<p>Whatever your company is most known for should go right here, whether that\'s bratwurst or baseball caps or vampire bat removal.</p><a href=\"#\" target=\"_self\">Learn More</a>		 \r\n				<a href=\"#\" target=\"_self\" itemprop=\"url\">\r\n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/home-2-1-1.jpg\" alt=\"home-2\" itemprop=\"image\"  />\r\n				</a>\r\n	<h3><a href=\"#\" target=\"_self\">Product / Service #2</a></h3>		\r\n			<p>What\'s another popular item you have for sale or trade? Talk about it here in glowing, memorable terms so site visitors have to have it.</p><a href=\"#\" target=\"_self\">Learn More</a>		 \r\n				<a href=\"#\" target=\"_self\" itemprop=\"url\">\r\n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/home-3-1-1.jpg\" alt=\"home-3\" itemprop=\"image\"  />\r\n				</a>\r\n	<h3><a href=\"#\" target=\"_self\">Product / Service #3</a></h3>		\r\n			<p>Don\'t think of this product or service as your third favorite, think of it as the bronze medalist in an Olympic medals sweep of great products/services.</p><a href=\"#\" target=\"_self\">Learn More</a>		 \r\n	<h3>Your Main Message</h3>\r\n<p>Use this space to tell people what your company does and why and how it does it. What\'re you known for? Who likes you? What\'s your number one competitive advantage?</p>\r\n<p>Include all the things that make your business unique and better than the competition. Do you have a patented 13-step process for taxidermy that results in the most lifelike stuffed owls? You gotta mention that.</p>\r\n<p>Other good things to weave into this copy include: awards won, distinctions given, number of products sold, company philosophy (just keep it short), interesting company history bits, and anything that makes a reader think you\'d be awesome to do business with.</p>\r\n				<img src=\"http://hopkinsmedreview.org/wp-content/uploads/2017/01/home-4-1-1.jpg\" alt=\"home-4\" itemprop=\"image\"  />\r\n		<h3>Next Steps...</h3>\r\n		<p>This is should be a prospective customer\'s number one call to action, e.g., requesting a quote or perusing your product catalog.</p>\r\n	<a href=\"\" target=\"_self\" role=\"button\">\r\n						Call to Action\r\n					</a>\r\n", "Home", "", "publish", "closed", "closed", "", "home", "", "", "2017-01-16 19:25:24", "2017-01-17 02:25:24", "", "0", "http://hopkinsmedreview.org/?page_id=156", "0", "page", "", "0");
