INSERT INTO `wp_a55jrcpgkb_posts` VALUES("364", "1", "2017-01-17 15:44:16", "2017-01-17 22:44:16", "<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">A doctor treats</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">A doctor heals</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">But does a doctor greet?</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">Will the doctor feel?</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">The doctor might treat, but who are they treating?</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">The doctor sees the illness</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">Not the person its defeating</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">Everything has changed</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">Before bills, medicine, and pills</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400;\">There was only a doctor and a patient</span></p>", "A Doctor and a Patient", "", "inherit", "closed", "closed", "", "362-revision-v1", "", "", "2017-01-17 15:44:16", "2017-01-17 22:44:16", "", "362", "http://hopkinsmedreview.org/362-revision-v1/", "0", "revision", "", "0");
